function init() {

    const providerOptions = {
        walletconnect: {
            package: WalletConnectProvider,
            options: {
                infuraId: "c0db0b85222f4f5c82dd2bed1fc843f9",
            }
        }
    };

    web3Modal = new Web3Modal({
        cacheProvider: false,
        providerOptions,
    });

}

async function mintNow(final_cost, mint_count) {
    var final_wei_Val = (final_cost * Math.pow(10, 18)).toFixedSpecial(0)
    await contract.methods.mint(userAddress, String(mint_count))
        .send({
            from: userAddress,
            value: final_wei_Val
        }).then(function (res, err) {
            $('#mintBtn').hide()
            if (res) {
                // Success Line
                $('#mintMSG').html("We're happy to say<br/>that you are now an NFT Holder.")
            } else {
                $('#mintMSG').html("Minting Failed!")
            }
        });
}

async function switch_network(chainId) {
    await window.ethereum.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId: web3.utils.toHex(chainId) }]
    });
}

function check() {
    web3.eth.getAccounts().then(async (tx) => {
        if (tx[0] !== undefined) {
            userAddress = tx[0]

            web3.eth.net.getId().then(async (netId) => {
                if (netId === chainId) {

                    $('#mintBtn').html('.....')

                    // run the mint box
                    await mintNow(mint_cost, 1)

                } else {

                    await switch_network(chainId)

                }
            });
        }
    });
}

async function connectweb3() {
    try {
        provider = await web3Modal.connect();
        web3 = new Web3(provider);
        contract = new web3.eth.Contract(t_abi, t_address);
        check()
    } catch (e) {
        console.log("Could not get a wallet connection", e);
        return;
    }

    provider.on("accountsChanged", (accounts) => {
        check()
    });

    provider.on("chainChanged", (chainId) => {
        check()
    });

    provider.on("networkChanged", (networkId) => {
        check()
    });

}


$(document).ready(() => {
    init();

    $('#mintBtn').click(async () => { await connectweb3(); })
});



Number.prototype.toFixedSpecial = function (n) {
    var str = this.toFixed(n);
    if (str.indexOf("e+") === -1) return str;

    str = str
        .replace(".", "")
        .split("e+")
        .reduce(function (p, b) {
            return p + Array(b - p.length + 2).join(0);
        });

    if (n > 0) str += "." + Array(n + 1).join(0);

    return str;
};